INSERT INTO `article` VALUES ('38', 'Yii2.0框架学习笔记（一）', 'Windows下安装：composer create-project --prefer-dist yiisoft/yii2-app-basic basic 1、关闭CSRF验证，在config/web.php文件下', null, '/upload/article/20190303083036.jpg', '<p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">一、Windows下安装：</span></strong></span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">composer create-project --prefer-dist yiisoft/yii2-app-basic basic</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">1、关闭CSRF验证</span></strong><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">在config/web.php文件下</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&#39;components&#39; =&gt; [</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &#39;request&#39; =&gt; [</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;enableCsrfValidation&#39; =&gt; false, &nbsp; &nbsp; &nbsp; &nbsp;//关闭post参数csrf验证</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;parsers&#39; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;=&gt; [</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;application/json&#39; =&gt; &#39;yii\\web\\JsonParser&#39;,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;application/xml&#39; &nbsp;=&gt; &#39;yii\\web\\JsonParser&#39;,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ],</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; ],</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"><span class=\"Apple-tab-span\" style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; white-space: pre;\"></span>...</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">]</span></p><p><br/></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">2、切换数据库</span></strong></span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&#39;db&#39; =&gt; $db,//默认配置</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&#39;db1&#39; =&gt; [</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;class&#39; =&gt; &#39;yii\\db\\Connection&#39;,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;dsn&#39; =&gt; &#39;mysql:host=localhost;dbname=o2o&#39;,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;username&#39; =&gt; &#39;root&#39;,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;password&#39; =&gt; &#39;&#39;,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;charset&#39; =&gt; &#39;utf8&#39;,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; ],</span></p><p><br/></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\"><strong><span style=\"font-size: 14px; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">3、返回响应数据格式</span></strong></span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">新建BaseController控制器，继承Controller</span></p><p><br/></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&lt;?php</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">namespace app\\controllers;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">use yii\\web\\Controller;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">use yii\\web\\Response;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">use yii\\filters\\ContentNegotiator;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">use yii\\helpers\\ArrayHelper;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">class BaseController extends Controller{</span></p><p><br/></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; /**</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp;* @return array</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp;*/</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; public function behaviors()</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; {</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; return ArrayHelper::merge(parent::behaviors(), [</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;contentNegotiator&#39; =&gt; [ &nbsp;//配置响应格式 &nbsp;&nbsp;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;class&#39; &nbsp; =&gt; ContentNegotiator::className(),</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;formats&#39; =&gt; [</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;application/json&#39; =&gt; Response::FORMAT_JSON,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &#39;application/xml&#39; &nbsp;=&gt; Response::FORMAT_JSON,</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ],</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ],</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; &nbsp; &nbsp; ]);</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">&nbsp; &nbsp; }</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\">}</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"></span></p><hr/><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">array_pop &nbsp; &nbsp; &nbsp; &nbsp;将数组最后一个单元弹出（出栈）</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">array_shift &nbsp; &nbsp; &nbsp;将数组开头的单元移出数组&nbsp;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">array_unshift &nbsp; &nbsp;在数组开头插入一个或多个单元</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">array_push &nbsp; &nbsp; &nbsp; 将一个或多个单元压入数组的末尾（入栈）&nbsp;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">array_unique &nbsp; &nbsp; 移除数组中重复的值&nbsp;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">array_map &nbsp; &nbsp; &nbsp; &nbsp;— 将回调函数作用到给定数组的单元上</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">$b &nbsp;= &nbsp;array_map ( &quot;cube&quot; , &nbsp;$a );</span></p><p><br/></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">$str = &#39;10月考核表 -薛明明.xlsx&#39;;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">if (strpos($str, &#39; &#39;) !== false) {</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">&nbsp; &nbsp; $str = str_replace(&#39; &#39;, &#39;\\ &#39;, $str);</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">}</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">echo $str . &#39;&lt;br&gt;&#39;;</span></p><p><br/></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">json_decode()接受一个 JSON 格式的字符串并且把它转换为 PHP 变量&nbsp;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">json_encode — 对变量进行 JSON 编码 json_decode($content,true) &nbsp; &nbsp;如果第二个参数为true，则转为数组，否则是对象</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">file_put_contents — 将一个字符串写入文件 &nbsp;第三个参数FILE_APPEND &nbsp;是追加</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">&nbsp;$i = 0;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">do {</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">&nbsp; &nbsp; echo $i;</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">&nbsp; &nbsp; $i++;</span></p><p><br/></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 16px;\">} while ($i &gt; 5); &nbsp;保证都会执行一次do里边的循环体，在while里判断，如果为true，则继续执行循环体。</span></p><p style=\"line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 16px;\"></span></p>', '3', 'checked', 'checked', '2019-01-30 08:30:38', '5', '20', null);
INSERT INTO `article` VALUES ('39', '余生，找一个宠你的人 ', '很喜欢作家苏芩的一句话说：一个人爱你，会在心里时时记挂着你，一个人很爱很爱你，会在平时处处纵容着你。只有那些把你看得比他自己还重要的人，才会宠着你。', null, '/upload/article/20181215173948.jpg', '<p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px;\">很喜欢作家苏芩的一句话说：一个人爱你，会在心里时时记挂着你，一个人很爱很爱你，会在平时处处纵容着你。只有那些把你看得比他自己还重要的人，才会宠着你。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><img src=\"/ueditor/php/upload/image/20190303/1551573178383300.jpg\" title=\"1551573178383300.jpg\" alt=\"1551573178383300.jpg\" width=\"\" height=\"\"/></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px; color: rgb(38, 38, 38);\"><strong>相爱的人适合一起风花雪月，宠你的人才适合一起慢慢变老。</strong></span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px;\">万千世界，茫茫人海，遇到喜欢你的人，或者遇到你喜欢的人，其实都不稀罕，但能够遇到一个宠你的人，真的太难得了。</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px;\">能有这样一个人，陪你笑，陪你闹，知你冷暖，懂你悲欢，真的是人世间，最温暖的烟火幸福。</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px;\">一辈子不长，要找一个愿意宠你的人在一起。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><img src=\"/ueditor/php/upload/image/20190303/1551573190325378.jpg\" title=\"1551573190325378.jpg\" alt=\"1551573190325378.jpg\" width=\"\" height=\"\"/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px; color: rgb(38, 38, 38);\"><strong>一个真正喜欢你的人，一定会把你当成个小孩子，把你的好记在心里，担心你照顾不好自己，你喜欢的、你在意的，都替你记着，你看不到的小细节，也帮你留意着。</strong></span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px;\">其实女人这辈子想要的，无非就是一个真心实意宠着自己的人，是一个能够在世事沧桑面前始终小心呵护着自己初心的人。</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px;\">霸道给你，温柔给你，陪伴给你，呵护给你，懂给你，宠也给你。</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px;\">情不知其所起，一往而深。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px; color: rgb(38, 38, 38);\"><strong>愿你强大到无需被人疼被人爱，却也幸运到有人懂有人宠，愿有一人，把你捧在手心，免你惊，免你苦，免你无枝可依。</strong></span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-size: 14px; color: rgb(38, 38, 38);\"><strong>余生不长，要找一个愿意宠你的人在一起。</strong></span></p><p><br/></p>', '1', 'checked', 'checked', '2019-03-03 21:56:32', '22', '44', null);
INSERT INTO `article` VALUES ('43', '2018 初雪，我爱小丸子', '2018初雪，西安的第一场雪，有人说，和爱人在雪下漫步，一不小心就白了头。可是，你戴着帽子......', null, '/upload/article/20190303082050.jpg', '<p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">2018初雪，西安的第一场雪，有人说，和爱人在雪下漫步，一不小心就白了头。可是，你戴着帽子......</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20190303/1551572632879974.jpg\" title=\"1551572632879974.jpg\" alt=\"1551572632879974.jpg\" width=\"\" height=\"\"/></span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">西安，是一个我不喜欢的城市之一，但是，这次的雪景下的很漂亮。晚间，在霓虹灯的衬托下，显得格外美丽。于是乎，拿起手机，赶紧拍照。（拍摄地点：唐遗址公园）</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20190303/1551572608302764.jpg\" title=\"1551572608302764.jpg\" alt=\"1551572608302764.jpg\" width=\"\" height=\"\"/></span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">门前的雪一大片，肉嘟嘟的小手丫。我对雪有独特的触雪情结。记得多年前，在哈尔滨的时候，那个雪真是冰天雪地，白茫茫的一片。正值圣诞节，我特别喜欢的一首歌《Christmas In My Heart》，配此情景，真是很享受的feel。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">这些年，却从未有过当初的那种feel了...</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><img src=\"/ueditor/php/upload/image/20190303/1551572708682403.jpg\" title=\"1551572708682403.jpg\" alt=\"1551572708682403.jpg\" width=\"\" height=\"\"/></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">西安，初雪茫茫，和喜欢的人徒步在高新的</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">每一条街道</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">相识，相知，相恋，相爱</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">执子之手，与子偕老</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190303/1551572723906219.jpg\" title=\"1551572723906219.jpg\" alt=\"1551572723906219.jpg\" width=\"\" height=\"\"/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">傍晚，大片大片的雪花，从昏暗的天空中纷纷扬地飘落下来。霎时间，道路交通公园，全都笼罩在白蒙蒙的大雪之中。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">西安的冬天似乎最像北方的冬天，干燥，寒冷，灯光的光芒就跟银针一般，照在脸颊上，竟有些砭骨般的疼痛。我用和手指在地面写过一行字，送给小丸子。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20190303/1551572807191925.jpg\" title=\"1551572807191925.jpg\" alt=\"1551572807191925.jpg\" width=\"\" height=\"\"/></span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">下了一夜的雪，大地都披上了银白色的大衣，把整个高新区变成了白色的世界。道路结了冰，我无法驾着小蓝。早早的起床，拉着小丸子的手，一步一个脚印踏在雪上，美美哒。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><img src=\"/ueditor/php/upload/image/20190303/1551572778633636.jpg\" title=\"1551572778633636.jpg\" alt=\"1551572778633636.jpg\" width=\"\" height=\"\"/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">人生，遇见，相爱相伴一生，简简单单幸福。2018年，愿我和小丸子结为连理，不负众之所托。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><img src=\"/ueditor/php/upload/image/20190303/1551572746125640.jpg\" title=\"1551572746125640.jpg\" alt=\"1551572746125640.jpg\" width=\"\" height=\"\"/></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">待到黄昏在相遇，君开心我随意。</span></strong></p>', '1', 'checked', 'checked', '2018-01-18 21:56:07', '539', '30', null);
INSERT INTO `article` VALUES ('44', 'JQuery判断radio是否选中和获取值', '使用checked属性判断选中radio不能用checked相等来判断，只用用true来判断。    ', null, '/upload/article/20190303081626.png', '<p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;!doctype html&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;html&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;head&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;meta charset=&quot;UTF-8&quot;&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;title&gt;Document&lt;/title&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;script type=&quot;text/javascript&quot; src=&quot;jquery-1.8.3.min.js&quot;&gt;&lt;/script&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;/head&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;body&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;form action=&quot;&quot;&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;input type=&quot;radio&quot; name=&quot;btn&quot; id=&quot;btn&quot; value=&quot;黑龙江省哈尔滨市松北区松浦街道2468号黑龙江科技大学&quot;&gt;黑龙江省哈尔滨市松北区松浦街道2468号黑龙江科技大学&lt;br&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;input type=&quot;radio&quot; name=&quot;btn&quot; id=&quot;&quot; value=&quot;西安交通大学&quot;&gt;西安交通大学</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;/form&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;button id=&quot;submit&quot;&gt;结算&lt;/button&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;/body&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;script type=&quot;text/javascript&quot;&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">一、利用获取选中值判断选中</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">$(function(){</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">//默认第一个选中</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">$(&#39;#btn&#39;)[0].checked=&quot;checked&quot;;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">$(&#39;#submit&#39;).click(function(){</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">var val=$(&#39;input:radio[name=&quot;btn&quot;]:checked&#39;).val();</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if(val==null){</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(&quot;什么也没选中!&quot;);</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; return false;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; else{</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; alert(val);</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">});</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">})</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;/script&gt;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;/html&gt;</span></p><p style=\"text-indent: 2em;\"><br/></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong>二、使用checked属性判断选中</strong></span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">radio不能用“checked”相等来判断，只用用true来判断 &nbsp; &nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">代码如下:</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;script type=&quot;text/javascript&quot;&gt;<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(function () {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; $(&quot;input&quot;).click(function () {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; if ($(this).attr(&quot;checked&quot;)) {<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; alert(&quot;选中了&quot;);<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; }<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; });<br/>&nbsp;&nbsp;&nbsp; &lt;/script&gt;<br/>&lt;/head&gt;<br/>&lt;body&gt;<br/>&lt;input type=&quot;radio&quot;/&gt;<br/>&lt;/body&gt;<br/>&lt;/html&gt;</span></p><p><br/></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong>三、jquery获取radio单选按钮的值&nbsp;&nbsp;</strong></span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">另：判断radio是否选中并取得选中的值&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">如下所示：&nbsp;<span style=\"font-family: 宋体, SimSun; text-indent: 2em;\">代码如下:</span></span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">function checkradio(){&nbsp;<br/>var item = $(&quot;:radio:checked&quot;);&nbsp;<br/>var len=item.length;&nbsp;<br/>if(len&gt;0){&nbsp;<br/>&nbsp; alert(&quot;yes--选中的值为：&quot;+$(&quot;:radio:checked&quot;).val());&nbsp;<br/>}&nbsp;<br/>}&nbsp;</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><strong>四、设置单选按钮被选中</strong></span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">代码如下:</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><a href=\"https://www.cnblogs.com/gallop/p/6912290.html\" target=\"_blank\" title=\"单选框是否被选中\" style=\"text-decoration: underline; font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">$(&quot;input[type=radio]&quot;).attr(&quot;checked&quot;,&#39;2&#39;);//设置value=2的项目为当前选中项&nbsp; &nbsp;</span></a></p>', '3', 'checked', 'checked', '2019-03-03 08:16:34', '11', '22', null);
INSERT INTO `article` VALUES ('45', 'PHP十个常用字符串函数（超实用！）', '1、strstr  返回haystack字符串从 needle 第一次出现的位置开始到 haystack 结尾的字符串。如果为发现返回false 如果第三个参数为true，则返回之前的字符串。', null, '/upload/article/20190303081832.jpg', '<p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-align: justify; text-indent: 2em;\">1、strstr&nbsp; 返回haystack字符串从 needle 第一次出现的位置开始到 haystack 结尾的字符串。如果为发现返回false 如果第三个参数为true，则返回之前的字符串。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">2、strrchr — 该函数返回 haystack 字符串中的一部分，这部分以 needle 的最后出现位置开始直到 haystack 末尾。若是字符串取第一个字符，未被找到，返回 FALSE 。&nbsp;</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">3、strpos — 查找字符串首次出现的位置 从0开始，如果没找到 needle，将返回 FALSE 。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">4、strrpos — 计算指定字符串在目标字符串中最后一次出现的位置 从0开始， 如果没找到 needle，将返回 FALSE 。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">5、substr 返回字符串 string 由 start 和 length 参数指定的子字符串,如果没有提供 length，返回的子字符串将从 start 位置开始直到字符串结尾</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">6、substr_count 返回子字符串needle 在字符串 haystack 中出现的次数。注意 needle 区分大小写。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">7、str_replace ( mixed $search（替换谁） , mixed $replace （替换成什么）, mixed $subject（目标字符串） [, int &amp;$count ] )&nbsp;</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">例如：str_replace(&quot;{code}&quot;, $code, $conf[&#39;sms_text&#39;])</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">8、str_shuffle() 函数随机打乱一个字符串</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">9、str_split — 将字符串转换为数组 array str_split ( string $string [, int $split_length = 1 ] ) 若length大于1，则以length长度分割成数组</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">10、strrev 返回 string 反转后的字符串。&nbsp;</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">str_repeat() 返回 input 重复 multiplier 次后的结果。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"></span></p><hr/><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-indent: 2em;\">PHP定界符</span><br/></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;&lt;&lt; EOF</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">EOF;</span></p><p><br/></p>', '3', 'checked', 'checked', '2019-03-03 08:18:38', '59', '29', null);
INSERT INTO `article` VALUES ('46', '2016等待，一场雪的季节', '入夜，风瑟瑟寒凉。空气中飘来淡淡雪的气息，这个季节，适合披上一个厚的披肩，捧一杯暖茶。眉间眼角带一丝暖意，读着自己的文字，品着字里行间的落寞。', null, '/upload/article/20190303080858.jpg', '<p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">入夜，风瑟瑟寒凉。空气中飘来淡淡雪的气息</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">这个季节，适合披上一个厚的披肩，捧一杯暖茶。眉间眼角带一丝暖意，读着自己的文字，品着字里行间的落寞。</span></p><p style=\"text-align: left; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px; text-align: justify; text-indent: 2em;\">这季节的轮回，也只是轻轻的低了一下眉。</span></p><p style=\"line-height: 2em; text-indent: 0em; text-align: center;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\"><img src=\"/ueditor/php/upload/image/20190303/1551571934560882.jpg\" title=\"1551571934560882.jpg\" alt=\"1519200584840179.jpg\"/></span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">便从姹紫嫣红的热闹中，倏然沉寂下来。安静了许多。</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">一场雪，与冬有约。</span></strong></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190303/1551571983336151.jpg\" title=\"1551571983336151.jpg\" alt=\"1519200610446411.jpg\"/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">很难想象，是要多么的坚强和执着才能让这晶莹剔透的柔柔弱弱的雪花，把巍峨的群山包得银装素裹；把干枯的树木打扮的富有诗意；把空空旷旷的原野赋予一种神秘和希望。</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">有人说，这个季节过于简单了，简单到只有黑白。其实，生命的底色何尝不是简单的，如若所有的繁杂都能删繁就简，是不是心也不会累？</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">要贪恋那繁花似锦的季节，也不要因为冬的苍白而失去了美好的期待，生命的路上，越简单越丰盈，不是吗？</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190303/1551572008893463.jpg\" title=\"1551572008893463.jpg\" alt=\"1519200493347656.jpg\"/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">等一场雪来，每一朵雪花都那么安静</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">忽然就觉得，可以听雪的季节是这么的美好。在那些被喧闹掩埋的角落里，给生命留一些独白，简单明了快乐安然，留一些素色的烟火沉积过往，踽踽独行。</span></p><p><br/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; font-size: 14px;\">一场雪来，不如说是等一片心境。心若简单，一切都简单。不是吗？</span></p><p><br/></p>', '1', 'checked', 'checked', '2016-09-23 08:13:40', '5', '19', null);
INSERT INTO `article` VALUES ('47', 'PHP 面试编程题集合', '1、交换两个数组的键值并输出：$a = [\'A\' => 1, \'B\' => 2, \'C\' => 3];$b = [\'A\' => \'X1\', \'B\' => \'X2\', \'C\' => \'X3\'];', null, '/upload/article/20190303083958.jpg', '<p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong>1、交换两个数组的键值并输出：</strong></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">$a = [&#39;A&#39; =&gt; 1, &#39;B&#39; =&gt; 2, &#39;C&#39; =&gt; 3];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">$b = [&#39;A&#39; =&gt; &#39;X1&#39;, &#39;B&#39; =&gt; &#39;X2&#39;, &#39;C&#39; =&gt; &#39;X3&#39;];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">//输出 $a = [&#39;X1&#39;=&gt;1,&#39;X2&#39;=&gt;2,&#39;X3&#39;=&gt;3];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">function change($a, $b)</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">{</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; if (empty($a) || empty($b) || !is_array($a) || !is_array($b)) exit(&#39;数组不能为空!&#39;);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; $data = [];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; $k = array_values($a);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; $v = array_values($b);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; for ($j = 0; $j &lt; count($v); $j++) {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; $data[$v[$j]] = $k[$j];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; }</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; return $data;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">}</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">//var_dump(change($c, $d));</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong>2、自定义方法截取文件的后缀名：</strong></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">$ext = &quot;Index.class.php&quot;;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">function getFileExt($filename)</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">{</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; return substr(strrchr($filename, &#39;.&#39;), 1);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">}</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">//$mail = &#39;251630332@qq.com&#39;;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">$mail = &#39;&#39;;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">//echo substr_count($mail,2);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong>3、本人面试面试官出的编程题：一个邮箱字符串，让你输出每一个字母或数字在邮箱中的出现的次数，不区分大小写。</strong></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">function getNum($mail)</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">{</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; if (empty($mail)) exit(&#39;邮箱不能为空!&#39;);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; $info = str_split(strtolower($mail));</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; $count = count($info);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; $data = [];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; for ($i = 0; $i &lt; $count; $i++) {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; $data[$i] = $info[$i] . &#39;:&#39; . substr_count(strtolower($mail), $info[$i]) . &#39;;&#39;;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; }</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; return implode($data);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">}</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">//echo getNum($mail);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong>4、冒泡排序：</strong></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">$arr =[1,22,2,65,42,8,14,55,3,21];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">function Order($arr){</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; $temp =&#39;&#39;;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp;for ($i=0;$i&lt;count($arr)-1;$i++){</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp;for ($j=0;$j&lt;count($arr)-1-$i;$j++){</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;if($arr[$j]&gt;$arr[$j+1]){</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$temp = $arr[$j];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$arr[$j] = $arr[$j+1];</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;$arr[$j+1] =$temp;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;}</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp;}</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp;}</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; return $arr;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">}</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">//var_dump(Order($arr));</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong>5、求$a,$b的差集，不允许使用array_diff()</strong></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp;public function diff1($a, $b)</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; $arrTMP = $a;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; foreach ($a as $k1 =&gt; $v1) {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; foreach ($b as $k2 =&gt; $v2) {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; if ($v1 == $v2) {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; unset($arrTMP[$k1]);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; }</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; return $arrTMP;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; }</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong>6、给一个txt文件内容加上行号</strong></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">public function text()</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; $content = file(PATH_TXT);//file — 把整个文件读入一个数组中</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; foreach ($content as $k =&gt; &amp;$v) {</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; $v = ($k + 1) . &#39; &#39; . $v;</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; file_put_contents(&#39;a.txt&#39;, $v, 8);</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; &nbsp; &nbsp; }</p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\">&nbsp; &nbsp; }</p><p><br/></p>', '3', 'checked', '', '2018-05-03 08:40:00', '5', '14', '2019-03-14 19:28:26');
INSERT INTO `article` VALUES ('48', '官宣：世界和你刚刚好', '2018年7月30日，农历6月18日，被我家小丸子叫成“年中”，这一天，结束了君航之恋整整561天，大航子和小丸子领结婚证啦！在爱情的长河里，我们彼此珍惜，真诚相待，相濡以沫，换来一辈子的责任和担当，余生长漫漫，且行且珍惜', null, '/upload/article/20190303084511.jpg', '<p style=\"text-indent: 2em; text-align: justify; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">2018年7月30日，农历6月18日，被我家小丸子叫成“年中”，这一天，结束了<a href=\"http://www.jhlfs.top\" target=\"_blank\">君航之恋</a>整整<span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(255, 0, 0);\"><strong>561</strong></span>天，</span><span style=\"color: rgb(255, 0, 0);\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; text-decoration: underline;\">大航子和小丸子领结婚证啦！</span></strong></span><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">在爱情的长河里，我们彼此珍惜，真诚相待，相濡以沫，换来一辈子的责任和担当，余生长漫漫，且行且珍惜。</span></p><p style=\"text-indent: 2em; text-align: justify; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><br/></span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/ueditor/php/upload/image/20190303/1551573852135711.jpg\" title=\"1551573852135711.jpg\" alt=\"1551573852135711.jpg\" width=\"\" height=\"\"/>&nbsp;&nbsp;&nbsp;<br/></span></p><p style=\"text-indent: 2em; text-align: justify; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"></span></p><p style=\"text-align: center; line-height: 2em;\"><img src=\"/ueditor/php/upload/image/20190303/1551573771937591.jpg\" title=\"1551573771937591.jpg\" width=\"\" height=\"\" alt=\"1551573771937591.jpg\"/></p><p style=\"text-align: center; line-height: 2em;\"><img src=\"/ueditor/php/upload/image/20190303/1551573793232208.jpg\" title=\"1551573793232208.jpg\" alt=\"1551573793232208.jpg\" width=\"\" height=\"\"/></p><p style=\"text-align: center; line-height: 2em;\"><img src=\"/ueditor/php/upload/image/20190303/1551573881722320.png\" title=\"1551573881722320.png\" alt=\"1551573881722320.png\" width=\"\" height=\"\"/></p><p style=\"text-align: center\"><br/></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">以后，在我的生命里，大航子会时刻陪伴在小丸子身边，不离不弃，你的可爱、童真、温文尔雅、体贴大方，实属难得的美丽姑娘，大航子此生能和小丸子结为连理，真可谓前世修来的福气。</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 176, 240);\">因为刚好遇见你...</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 176, 240);\">你深深的印在脑海里，</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 176, 240);\">世界和你刚刚好，</span></p><p style=\"text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;; color: rgb(0, 176, 240);\">恰到好处。</span></p>', '1', 'checked', 'checked', '2018-07-30 20:37:13', '1006', '77', null);
INSERT INTO `article` VALUES ('49', '乡愁', '记得八年前，第一次离开故土去新城市上学，这也是我第一次去这么远的地方，当我慢慢的消失在奶奶的视线里，看着她瘦小身影和那种难舍的面部表情......', null, '', '<p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">记得八年前，第一次离开故土去新城市上学，这也是我第一次去这么远的地方，当我慢慢的消失在奶奶的视线里，看着她瘦小身影和那种难舍的面部表情，那一刻，眼泪止不住的流，舍不得奶奶，舍不得土生土长的地方，这大概就是我去远方的乡愁。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">或许跟自己的经历有关，我对家乡的思念基本没有概念除了奶奶的恩情。我从小就很独立，也是奶奶一手将我拉扯大，奶奶就是我唯一的牵挂。我也成为她的牵挂。在她的心里面，整天惦记着孙子，记得上一次二弟回家，刚踏入家门奶奶见了便激动起来，不小心摔倒了头一下子撞在鞋架上，额头磕破了口子，后来去诊所包扎了。虽然有这不和谐的小插曲，但在她眼里，孙子回来了，心里就是很开心了，恐怕早就忘了自己受了伤。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">每一次的回家，心情总是复杂的，道不出来的感觉，有一次，送对象回家，同样的场景，让我有了天壤之别的感觉，我真的羡慕那些人，一回来父母就出门欢迎，高兴帮忙拿行李，嘘寒问暖……等，场面很是温馨，然而，作者并没有，除了满笑欢喜的奶奶出来相迎，虽然心凉但也有一丝安慰。家，对我来说，很陌生，不能太任性，大多数情况，我是不愿回这冰冷的家，再怎么不想回，可是心里一直牵挂着奶奶，每次梦中醒来，梦见奶奶与世长辞，我的心是何等的失落，虽然小丸子说，做这种梦的意思是给老人长寿，或许是吧。只有想她的时候才回去看看她。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">人都有思念的和心里最牵挂的人，乡愁，一种思念和牵挂，我出门在外，唯一的乡愁就是我的奶奶，然而，老天差点连我最后的乡愁给夺走了。去年9月份，奶奶在家不小心摔倒一直卧床不起，后来经过治疗未见起色，病情加重。当得知这个消息后，我脑海里犹如天塌了下来，我不能在这里表露出来自己的情绪，于是，我独自一人漫步小寨人行天桥上，也是黄昏十分，悲痛的心情一下子控制不住了，我蜷缩在一个小角落里，眼泪就是洪水泛滥，心里想着奶奶从小将自己拉扯大的心酸点点滴滴事情，突然觉得自己这辈子对不起她，无法报答她的恩情，于是我立即请假回家看她最后一面。</span></p><p style=\"text-align: center;\"><img src=\"/ueditor/php/upload/image/20190304/1551709975.jpg\" title=\"1551709975.jpg\" alt=\"psb.jpg\"/></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">当我出现她的身旁的时候，她躺在病床上，身上插满了管子，手背都是青一块紫一块的，望着她的脸颊，不得不让我心痛，她见到我似乎有话想说，由于插着氧气口罩，我们也听不清说什么，虽然什么也没有说，此时此刻，我都能明白她说什么，奶奶不不甘心走，因为她还没亲眼看到两个孙子结婚。所以她尽管在病床上但精神上一直和死神作斗争。最后，她被医院放弃了治疗，因为各器官衰竭，大夫已经回天乏术了。后来我们把她送回家里，陪她最后一程。那时候的我，姑姑，亲戚都在我家里，有好几次奶奶的呼吸停止了，……（不想再回忆细节，此处省略200字），尽管死神是如何的带她走，可是她用的毅力和精神打败了死神。奇迹就是这样发生了，在最后的几天里，奶奶病情好转，也开始吃饭了，再到后来，下地走路，说话，一点都不糊涂，就这样，她从鬼门关走了一遭。我终于送了一口气。我读大学的时候就开始远离家乡，就是靠着对奶奶的思念和精神上的寄托，才让我有了乡愁。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">有思念就走牵挂，有牵挂就有乡愁，现在的我还很年轻，可是奶奶已经不年轻了，没有太多的时间等待了，我只有回报她的就是解决我的个人婚姻大事，也是能了却她老人家最大的心事。这一次回来，晚上我和她睡在一个炕上，多想陪陪她，说说话，总是能回到对象这个问题，我大声给她说，对象有，国庆就在家办酒席，只见她露出了笑容，很慈祥，也很可爱。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">我是在这样的环境成长的，性格难免孤僻，在我身上发生的事情，我不会在我的下一代重演，我会做一个称职的父亲，有担当有责任的丈夫，一个完整的家庭，充满正能量和温暖，让我儿（海浪）或女儿（浪花）有更多的乡愁，不再像父亲一样只有唯一的乡愁。</span></p><p style=\"text-align: justify; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">几年后，我在外边，你在里边，你依旧是我的乡愁。</span></p><p style=\"text-align: right; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">铺邑村</span></p><p style=\"text-align: right; line-height: 2em; text-indent: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">2018年4月22日</span></p><p><br/></p>', '1', 'checked', '', '2018-04-22 22:34:10', '16', '13', null);
INSERT INTO `article` VALUES ('50', '回顾大学', '马上进入四月份了，在这里的温度依然那么的冷，在还剩下不到三个月的时间我们就要毕业了，想想大学四年一路走来不容易，仿佛看了一场“致青春”电影', null, '/upload/article/20190304224131.jpg', '<p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">马上进入四月份了，在这里的温度依然那么的冷，在还剩下不到三个月的时间我们就要毕业了，想想大学四年一路走来不容易，仿佛看了一场“致青春”电影，当然，我们在里边是主角，演绎着大学四年中发生的点点滴滴，又好像做了一个梦，漫长而又清晰。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/ueditor/php/upload/image/20190305/1551793977.jpg\" title=\"1551793977.jpg\" alt=\"222.jpg\"/></span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">那年高考后，我收到大学录取通知书，这个消息令我和家里高兴不已，怀着对大学的美好憧憬，我在父亲的陪同下来到了黑龙江科技大学，这个曾经让我抱有很多幻想的地方，如今我却梦寐以求的来到了这里。开始面对陌生的环境、陌生的室友我有点适应不过来，因为第一次来到哈尔滨，这边的气候很让我吃不消，记得大一经常光顾校医院了。经过一两个月的适应，我慢慢的适应大学生活，懂得怎么去和同学们及老师相处，另外我也对大学做好初步的计划，可是事与愿违，得到的和计划的总是有出入，回顾整个大学期间所做的事，还是有很多事情让我感动。下面我来具体的说明。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><img src=\"/ueditor/php/upload/image/20190305/1551794141.jpg\" title=\"1551794141.jpg\" alt=\"微信图片_20190305215617.jpg\"/></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">首先，说说大学生社团</span></strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">。刚在我们大一新生军训的时候，就有很对大学生社团在文化广场招新，当时的我很有想法去参加某个社团，于是我选了环保协会和科技协会，后来由于一些原因科技协会没能去复试，最后就进入了校环保协会，可能是和我所学的环境工程专业相关。我是秘书部干事，平时开例会签到、会议记录都是由我们来完成的，另外我积极参加协会各种活动，比如说大型环保服饰嘉年华、陪同会长去林大做环保思想交流会等，这些使我认识了很多朋友，也锻炼了我敢于表现自己的能力。记得我和会长、部长及其他干事为了筹划某个大型活动，我们集体在主楼西区地下室共同商量该怎么去做，该怎么去宣传等思想交流，这使我们干事和会长之间很亲密，就像和兄弟姐们一样，是一个环保大家庭，在这个大家庭里我们互帮互助、非常有爱。很快在环保社团的陪同下我轻松的过完我的大一时光。由于我很喜欢这个协会，所以我大二没有离开环保协会，而是选择继续留在环保协会，这一次我担任秘书部部长一职，同样的经历了和上届学长们干的事情，我带领我的干事继续做好本职工作，和他们又相处了一年，我承认，在环保待得两年里，我收获了很多，也改变了很多，我觉得大学生有必要参加社团好好的展示自己。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/ueditor/php/upload/image/20190306/1551872372.jpg\" title=\"1551872372.jpg\" alt=\"222.jpg\"/></span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">第二个顾名思义就是学习</span></strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">。这里我先坦白下自己，我承认我大学学习没有高三时那么用功，甚至连一半都不到，即使自己不怎么厌学但也同时学习着，我很庆幸我大学所有课程都通过了，当然也有几门课成绩在60分左有的，四年来，我在的综测成绩处于班级中等水平。在我潜意识里，我认为大学所有的课程都得学，但是走向工作岗位能用的的知识不到5%，我觉得重要的是学会学习的方法，这才是最关键的，另外我不得不承认，不乏有些人就是为了考试而学习，我呢，也不全是为考试而学习，我就是想多学点知识，多掌握点技能，这样为我以后面试打好基础。思修是我最喜欢听得课程，这门课程让我懂得了历史，懂得了自我价值观、人生观、审美观，完全改变了我的思维模式，所以我很喜欢这们课，更喜欢这门课的老师，因为她教的非常好。我学的是环境，方向偏水，我也喜欢治理污水，更喜欢看各种工艺治理污水的书，我们也实地参观过污水厂，每一个构筑物我都有亲眼见到，从浑浊的污水到清澈的出水，我感觉到了作为环境人士的骄傲，我愿意把我一生精力投入到我国治理污水行业中。我认为我所学这门技术是伟大的，是自豪的，治理污水，那是功在当代利在千秋，所以我得更加学好我的专业知识。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/ueditor/php/upload/image/20190304/1551710562.jpg\" title=\"1551710562.jpg\" alt=\"3.jpg\"/></span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">接着是关于友情</span></strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">。我们寝六个人，都来自五湖四海，每个人都有自己爱好、洁癖，为了更好的和室友们相处，我选择了包容、忍让、尊重，既然我都汇聚一个寝室成为好兄弟，那就是缘分，我们更得珍惜这份友情。即使我是这样想的，但有时候难免会发生口角，只有忍耐到了极限时才会出现干仗什么的，当然这算最糟糕的了。我和他们也有小吵小闹过，但这不是重点，重点是通过这点小吵小闹更好的让我们认识彼此，了解更深，自然关系更加的铁，这无疑是件好事。记得大二第一学期的时候，当我因病需要住院手术而发愁拿不出高昂的医药费时，我看到了室友们的热心帮忙，从寝室到班级的每一位同学对我伸出了援助指数，老师及辅导员都对我做了思想工作，因为我当时一个劲的想回家治疗，由于家里离学校很远，辅导员老师让我在这看病，不要考虑钱的问题，只管安心在这看病，这一次，我真心被辅导员及同学们打动了，他们一个个的为我捐钱，直到我做完手术，我的寝室兄弟轮班照顾我，这份兄弟情使我真的很难忘，班级同学也过来看我，在我病的时候，陪在我身边的不是我的亲人而是我的班级同学、我的室友，谢谢你们的细心照顾，为我补习功课，你们为我所做的一切，我是看到眼里感动在心里，在同学们的帮助下我很快康复出院了。通过这件事，我感觉到我们班就像一个大家一样，感受到家的温暖，在困难面前，不分你我，勇于伸出援救之手，你们好比我的家人一样对我关心的无微不至，让我顿时感觉到班级的强大。再次，我还要感谢我寝室兄弟，谢谢你们对我的包容，在我有病期间，真的有劳你们了。还有我要感谢我的辅导员老师，是他给我了信心，是他给我了力量，没有老师的开导就没有我现在康复的学习，总之，我们环境班就要像一家人一样互帮互助、团结一致。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><img src=\"/ueditor/php/upload/image/20190306/1551872438.jpg\" title=\"1551872438.jpg\" alt=\"66.jpg\"/></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">关于爱情。本来上大学期间不打算处对象，等工作以后再找吧，可是感情这东西谁也说不准。在大二的时候我认识了一个女孩，是因为她做出让我感动的事而喜欢上她的，于是和她正是交往了，这也是我的爱情生涯开始了，开始我们都互相喜欢彼此，可是处了三个月爱情便夭折了，为此我伤心几周，后来想想也没什么大不了的，还是好好的念书比什么都强。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/ueditor/php/upload/image/20190304/1551710727.jpg\" title=\"1551710727.jpg\" alt=\"微信图片_20190304224648.jpg\"/></span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">英语四级是大学里让我最头疼的事</span></strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">。我从大一刚上来的时候就开始着手准备四级，从第一学期开始考四级，考了好多好多次，每考一次不过受一次打击，这都打击多少回了都，曾经有过放弃的念头，可是最后也没放弃，我承认四级没过我自身也有原因，那就是没有用心去学英语，每次都报有侥幸过的心里，所以每次都没有过，后来渐渐的大四了，也面临找工作了，但凡好一点的单位都要求英语四级，我呢，面试也有七八回了，大部分简历筛选的时候被刷了，后来才意识到四级证的重要性，于是我这一回用心学一下，也给自己一个交代，记得大四第一学期期末的时候，班级所有同学回家的时候（除了考研的），只有我一个在寝室，白天图书馆学四级晚上还得学两个点，就那样坚持到了考试的前一天，回想起那段一个在寝室的时候太孤独了，连个说话人都没有，除了学习英语四级还是学习四级。这次的努力没有白费，我最终通过四级考试，这个消息让我抓狂、发疯了一周，我做梦都没想到我四级能过。四级证虽然是一张成绩单，但是他的重要性远比本身重要，作为大学生，最基本的英语四级必须要过，接着根据自己实力是否还要考英语六级，多学一点英语是对于我们终生受益的。</span></p><p style=\"text-align: center; line-height: 2em; text-indent: 0em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\"><img src=\"/ueditor/php/upload/image/20190304/1551710583.jpg\" title=\"1551710583.jpg\" alt=\"2.jpg\"/></span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">还有就是签约工作的事。从去年国庆后，我就已经走招聘会了，只要有招我们专业的招聘会我都会去，我在网上投简历、去哈尔滨人才市场及各大高校去面试，前后面试也有八九回，每一次面试失败我没有选择气馁，而是认真总结上一次失败原因，以好的心态迎接下一次的面试。最终，10月29日在东北林业大学面试成功签约。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">最后就是其他。</span></strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">在大学期间，曾参加寝室篮球赛，我们寝最终以3:2稳赢隔壁寝室，打篮球主要靠的是队员们的默契和团队合作能力，即使你一个人再强也没用，只有全部强了才算强。另外，我在校勤工助学干了两年，从组员升到组长，管理好自己工作，对组员负责，因为我在保洁岗工作细心、负责，最后被评为“勤工助学工作标兵”荣誉称号。在这期间，我还通过了国家计算机二级等级考试和驾照C1证。还有一件事，我每年都献全血2回，在哈尔滨献血已经累计1200mm，通过自己的一点绵薄之力来拯救更多人的生命，也可算是功德无量。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">大学四年虽美好而短暂，记录着我所有点点滴滴，每一件做过的事仿佛在脑海里昨天发生的一样，如今剩下不到三个月的时间里，我只想把毕业设计做好，因为毕业设计是对我大学所有的课程的总结，结合所学专业知识和校外实习进行有机整合，通过毕业设计，使我们与工作接轨，更好把理论知识应用到实践当中。我希望我以后不断的去学习，努力去成为污水工程师，为环境献出自己的一份力量。</span></p><p style=\"text-align: justify; text-indent: 2em; line-height: 2em;\"><span style=\"color: rgb(0, 112, 192);\"><strong><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">最后，我要感谢所有帮助过的老师，谢谢你们一直以来谆谆教诲，还有我的同班兄弟姐妹们，谢谢大学四年有你们相伴，使得我顺利完成大学学业。</span></strong></span></p>', '1', 'checked', 'checked', '2014-06-30 19:40:47', '132', '94', null);
INSERT INTO `article` VALUES ('51', 'PHP封装文件上传和文件下载类', 'PHP封装文件上传和文件下载类', null, '', '<p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&lt;?php</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">/**</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;* 功能：文件上传和文件下载</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;* Created by PhpStorm.</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;* User: ZH</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;* Date: 2018/10/18</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;* Time: 11:00</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp;*/</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">class UploadForm</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">{</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; static public $path = &quot;Upload&quot;;</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; public $size = 8;</span></p><p><br/></p><p><br/></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; /**</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp;* 功能：文件上传</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp;* @param $name &#39;文件名&#39;</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp;* @param $path &#39;上传临时目录&#39;</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp;*/</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; public function Upload($name, $path)</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; {</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; //检测文件目录是否存在，不存在创建</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; if (!is_dir(self::$path)) {</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; exec(&#39;mkdir &#39; . self::$path);</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; }</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; //文件名称非空判断</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; if (empty($name)) {</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; exit(&#39;请选择上传文件！&#39;);</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; }</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">//&nbsp; &nbsp; &nbsp; &nbsp; if (strpos($name, &#39; &#39;) !== false) {</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">//&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; exit(&#39;文件名不能有空格&#39;);</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">//&nbsp; &nbsp; &nbsp; &nbsp; }</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; if (preg_match(&#39;/[^a-zA-Z0-9_.]/&#39;, $name)) {</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; exit(&#39;文件名只允许包含数字母下划线！&#39;);</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; }</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; $ext = $this-&gt;getFileType($name);</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; //允许上传文件的格式</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, &quot;Microsoft YaHei&quot;;\">&nbsp; &nbsp; &nbsp; &nbsp; if (!in_array($ext, [&#39;xlsx&#39;, &#39;txt&#39;, &#39;jpg&#39;, &#39;php&#39;, &#39;png&#39;, &#39;chm&#39;, &#39;pdf&#39;])) {</span></p><p><br/></p><p style=\"line-height: normal;\"><span style=\"font-family: 微软雅黑, 